select t.* from (
	SELECT b.*,u.uid,u.mobile from app_bill_record b inner join app_user u on b.userId=u.id

		1 = 1
		AND u.uid=#{model.uid}
		AND u.mobile=#{model.mobile}
		AND b.busnessType=#{model.busnessType}
		AND b.createTime>=#{model.startTime}
		AND b.createTime<#{model.endTime}
		AND b.businessNumber=#{model.businessNumber}
		AND u.parentId=#{model.parentId}


	union all
	SELECT b.*,'代理' as uid,u.mobile from app_bill_record b inner join sys_user u on b.userId=u.id

		1 = 1
		AND u.uid=#{model.uid}
		AND u.mobile=#{model.mobile}
		AND b.busnessType=#{model.busnessType}
		AND b.createTime>=#{model.startTime}
		AND b.createTime<#{model.endTime}
		AND b.businessNumber=#{model.businessNumber}
		AND u.id=#{model.parentId}


) as t
ORDER BY t.createTime DESC
limit #{startRow}, #{pageSize};

    	SELECT count(1) from app_bill_record b left join app_user u on b.userId=u.id

       	 1 = 1
		AND u.uid=#{model.uid}
		AND u.mobile=#{model.mobile}
		AND b.busnessType=#{model.busnessType}
