select a.*,t.betTotal as betScore,(t.lotteryTotal+t.betTotal) as memberRs,t2.bus1 as agentProfit,b.agentName,t.betCount
  from sys_user a
 inner join sys_agent_setting b on a.agentLevelId = b.id
 left JOIN
	(select sum(case b.busnessType when 21 then b.balance when 23 then b.balance when 31 then b.balance when 33 then b.balance else 0 end) as betTotal,
			 sum(case b.busnessType when 22 then b.balance when 32 then b.balance else 0 end) as lotteryTotal,count(1) as betCount,
			 b.busnessType,b.userId,a.parentId from app_user a
	   LEFT JOIN app_bill_record b on a.id = b.userid

			1 = 1 and a.state='10'
			AND a.parentId=#{model.userId}
			AND b.createTime>=#{model.startTime}
			AND b.createTime<#{model.endTime}
			AND datediff(b.createtime,now())>=#{model.startDiff}
			AND datediff(b.createtime,now()) <=#{model.endDiff}

	  GROUP BY a.parentId
	) as t on a.id=t.parentId
left JOIN
	(select sum(case b.busnessType when 51 then b.balance else 0 end) as bus1,
			 0 as bus2,
			 b.busnessType,a.id from sys_user a
	   LEFT JOIN app_bill_record b on a.id = b.userid

		a.roleType='20'
		AND a.id=#{model.userId}
		AND a.mobile=#{model.mobile}
		AND b.createTime>=#{model.startTime}
		AND b.createTime<#{model.endTime}
		AND datediff(b.createtime,now())>=#{model.startDiff}
		AND datediff(b.createtime,now()) <=#{model.endDiff}

	  GROUP BY a.id
	) as t2 on a.id=t2.id

	a.roleType='20'
	 AND a.id=#{model.userId}

