SELECT bill.*,user.mobile,user.imgPath,user.nickName from app_bill_record bill left join app_user user on bill.userId=user.id
where userId=#{userId}
and busnessType IN

    #{item}

order by bill.createTime desc

		select ifnull(SUM(balance),0) from  app_bill_record

       	 1 = 1
		AND busnessType=#{model.busnessType}
		AND createTime>=#{model.startTime}

select a.* from sys_user a
 INNER JOIN
	(select sum(case b.busnessType when 21 then b.balance when 23 then b.balance when 31 then b.balance when 33 then b.balance else 0 end) as betTotal,
			 sum(case b.busnessType when 22 then b.balance when 32 then b.balance else 0 end) as lotteryTotal,
			 b.busnessType,b.userId,a.parentId from app_user a
	   LEFT JOIN app_bill_record b on a.id = b.userid

			1 = 1
			AND b.createTime>=#{model.startTime}
			AND b.createTime<#{model.endTime}

	  GROUP BY a.id
	) as t on a.id=t.parentId
INNER JOIN
	(select sum(case b.busnessType when 1 then b.balance else 0 end) as bus1,
			 sum(case b.busnessType when 2 then b.balance else 0 end) as bus2,
			 b.busnessType,a.id from sys_user a
	   LEFT JOIN app_bill_record b on a.id = b.userid

		a.roleType='20'
		AND a.id=#{model.userid}
		AND a.uid=#{model.uid}
		AND a.mobile=#{model.mobile}
		AND b.createTime>=#{model.startTime}
		AND b.createTime<#{model.endTime}

	  GROUP BY a.id
	) as t2 on a.id=t2.id
