from


    issueNo=#{issuNo} AND lotteryFlag=#{lotteryFlag}
    AND lotteryOne=#{digital}
    AND lotteryTwo=#{digital}
    AND lotteryThree=#{digital}
    AND lotteryFour=#{digital}
    AND lotteryFive=#{digital}
    AND lotterySix=#{digital}
    AND lotterySeven=#{digital}
    AND lotteryEight=#{digital}
