    AND lotteryTen=#{digital}

select t.*,u.uid,u.nickName from
    (SELECT a.id,a.issueNo,a.userId,a.betType,
            a.bettingContent as betContent,a.multiple as betAmount,b.flag as gameState
            ,a.lotteryFlag as state,a.winningAmount,a.createTime,a.businessNumber,
            (b.lotteryOne+b.lotteryTwo+b.lotteryThree+b.lotteryFour+b.lotteryFive) as lotteryResult
     FROM `app_time_betting` a LEFT JOIN app_time_lottery b on a.issueNo=b.issueNo

        1 = 1
        AND w.state=#{model.state}
        AND u.issueNo=#{model.issueNo}
        AND a.createTime>=#{model.startTime}
        AND a.createTime<#{model.endTime}
        AND a.businessNumber=#{model.businessNumber}

) as t
LEFT  join app_user u on t.userid=u.id

    1 = 1
    AND u.uid=#{model.uid}

ORDER BY t.createTime DESC

select count(id) from


    userId=#{userId} AND issueNo=#{issueNo}
    AND bettingContent=#{bettingContent}

select   from


    userId=#{userId} AND issueNo=#{issueNo}
    AND bettingContent=#{bettingContent}
