mapper.select:
       	SELECT b.*,u.uid,u.mobile from app_bill_record b left join app_user u on b.userId=u.id

       	 1 = 1
		AND u.uid=#{model.uid}
		AND u.mobile=#{model.mobile}
		AND b.currencyType=#{model.currencyType}
		AND b.busnessType=#{model.busnessType}
		AND b.createTime>=#{model.startTime}
		AND b.createTime<#{model.endTime}
		AND b.uuid=#{model.uuid}
		AND b.businessNumber=#{model.businessNumber}


		ORDER BY b.createTime DESC
		limit #{startRow}, #{pageSize};

    	SELECT count(1) from app_bill_record b left join app_user u on b.userId=u.id

       	 1 = 1
		AND u.uid=#{model.uid}
		AND u.mobile=#{model.mobile}
		AND b.currencyType=#{model.currencyType}
		AND b.busnessType=#{model.busnessType}
		AND b.createTime>=#{model.startTime}
		AND b.createTime<#{model.endTime}
		AND b.uuid=#{model.uuid}
		AND b.businessNumber=#{model.businessNumber}

SELECT bill.*,user.mobile,user.imgPath,user.nickName from app_bill_record bill left join app_user user on bill.userId=user.id

	 AND bill.userId=#{model.userId}
	 AND bill.currencyType=#{model.currencyType}
	 AND bill.busnessType=#{model.busnessType}

order by bill.createTime desc
